import React, { useState, useEffect } from "react";

const SearchComponent = () => {
  //setear los hooks de useStates
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState("");

  //   // funcino para traer los datos de la api
  const URL = "https://jsonplaceholder.typicode.com/posts";

  const showData = async () => {
    const response = await fetch(URL);
    const data = await response.json();
    console.log(data);
    setPosts(data);
  };

  //   // metodo de filtrado (2 formas)

  //   //funcion de busqueda
  useEffect(() => {
    showData();
  },[]);
  //   //renderizamos la vista
  return (
    <div className="container-fluid">
    <input value={search} type="text" palceholder="Buscar" className="form-control"></input>
      <table className="table table-bordered table-hover mt-5 shadow-lg">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
            {posts.map((post) => (
              <tr key = {post.id} >
                <td>{post.title}</td>
                <td>{post.id}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default SearchComponent;
