import React, { useState, useEffect } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";

const SearchComponent = () => {


  // funcino para traer los datos de la api
  const URL = "https://jsonplaceholder.typicode.com/posts";


  //setear los hooks de useStates
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState('');
  const [updated, setUpdated] = useState(message);




  const showData = async () => {
    const response = await fetch(URL);
    const data = await response.json();
    console.log(data);
    setPosts(data);
  };

  //funcion de busqueda

  const searcher = (e) => {
    setSearch(e.target.value);
    // console.log(e.target.value);
  };

  // metodo de filtrado (2 formas)
  const handleChange = (event) => {
    setMessage(event.target.value);
  };

//   guarda el valor del input
  const handleClick = () => {
    setUpdated(message);
  };



  let filtered_list = [];
  if (!search) {
    filtered_list = posts;
  } else {
    filtered_list = posts.filter((dato) =>
      dato.title.toLowerCase().includes(search.toLocaleLowerCase())
    );
  }

  useEffect(() => {
    showData();
  }, []);
  //   //renderizamos la vista
  return (
    <div className="container-sm">
      <Row>
        <Col>
          <input
            type="text"
            className="form-control"
            placeholder="Buscar"
            onChange={handleChange}
          />
        </Col>
        <Col className="text-end">
          <Button variant="outline-dark" onClick={searcher}>Buscar</Button>
        </Col>
      </Row>
      <table className="table table-bordered table-hover mt-3 shadow-lg">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {filtered_list.map((post) => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>{post.id}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SearchComponent;
