import React, { useRef, useState, useEffect } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import { FaBeer } from "react-icons/fa";

const SearchComponent = () => {
  // setear los hooks de useStates
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState("");
  const inputRef = useRef(null);
  const addDescription = useRef(null);
  const addName = useRef(null);

  //   --------------------------------------------------------------------------------------------------
  //   ----------------------------------------- FUNCIONES ----------------------------------------------
  //   --------------------------------------------------------------------------------------------------

  // traer los datos de la api
  const URL = "http://localhost:3001/api/getPosts";

  const showData = async () => {
    const response = await fetch(URL);
    const data = await response.json();
    console.log(data);
    setPosts(data);
  };

  //funcion de busqueda

  const handleClick = () => {
    setSearch(inputRef.current.value);
  };

  // metodo de filtrado (2 formas)

  let filtered_list = [];
  if (!search) {
    filtered_list = posts;
  } else {
    filtered_list = posts.filter((dato) =>
      dato.title.toLowerCase().includes(search.toLocaleLowerCase())
    );
  }

  //funcion de crear
  const handleChange = (e) => {
    console.log(e.target.value);
  };

  const handleCreate = () => {
     const newPost = {name: addName.current.value, description: addDescription.current.value} 
     console.log(newPost);
    // window.location.reload(true);


  };

  //   muestra la data
  useEffect(() => {
    showData();
  }, []);
  //   --------------------------------------------------------------------------------------------------
  //   ---------------------------------------------- VISTA----------------------------------------------
  //   --------------------------------------------------------------------------------------------------
  return (
    <div className="container-sm">
      {/* Buscador */}
      <Row>
        <Col>
          <input
            type="text"
            className="form-control"
            placeholder="Buscar"
            ref={inputRef}
            id="filter"
            name="filter"
          />
        </Col>
        <Col className="text-end">
          <Button variant="outline-dark" onClick={handleClick}>
            Buscar
          </Button>
        </Col>
      </Row>
      {/* tabla */}
      <table className="table table-bordered table-hover mt-3 shadow-lg">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {/* {filtered_list.map((post) => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>{post.id}</td>
              <td>
                <Button variant="danger" onClick={handleClick}>
                  Eliminar
                </Button>
              </td>
            </tr>
          ))} */}
        </tbody>
      </table>
      <Row>
        <Col md={5}>
          <input
            type="text"
            className="form-control"
            placeholder="Nombre"
            ref={addName}
            onChange={handleChange}
            id="name"
            name="name"
          />
        </Col>
        <Col md={6}>
          <input
            type="text"
            className="form-control"
            placeholder="Descripción"
            ref={addDescription}
            onChange={handleChange}
            id="description"
            name="description"
          />
        </Col>
        <Col md={1} className="text-end">
          <Button variant="outline-dark" onClick={handleCreate}>
            Crear
          </Button>
        </Col>
      </Row>
    </div>
  );
};

export default SearchComponent;
