import React, { useState, useEffect } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from 'react-bootstrap/Button';

const SearchComponent = () => {
  //setear los hooks de useStates
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState("");

  //   // funcino para traer los datos de la api
  const URL = "https://jsonplaceholder.typicode.com/posts";

  const showData = async () => {
    const response = await fetch(URL);
    const data = await response.json();
    console.log(data);
    setPosts(data);
  };

 

  //funcion de busqueda
  const searcher = (e) => {
    setSearch(e.target.value);
  // console.log(e.target.value);
  };
  let filtered_list = [];
  if(!search){
    filtered_list = posts
  } else {
    filtered_list = posts.filter( (dato)=> dato.name.toLowerCase().includes(search.toLocaleLowerCase()))
  }
  // metodo de filtrado (2 formas)

  useEffect(() => {
    showData();
  }, []);
  //   //renderizamos la vista
  return (
    <div className="container-sm">
      <Row>
        <Col>
        <input type="text" className="form-control" value={search} onChange={searcher}/>
          {/* <input
            value={search}
            type='text'
            placeholder="Buscar"
            className="form-control"
          /> */}
        </Col>
        <Col className="text-end">
        <Button variant="outline-dark">Buscar</Button>
        </Col>
      </Row>
      <table className="table table-bordered table-hover mt-3 shadow-lg">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>{post.id}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SearchComponent;
