import React, { useRef, useState, useEffect } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import { FaBeer } from "react-icons/fa";

const SearchComponent = () => {
  // setear los hooks de useStates
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState("");
  const inputRef = useRef(null);
  const addDescription = useRef(null);
  const addName = useRef(null);

  // funcion para traer los datos de la api
  const URL = "https://jsonplaceholder.typicode.com/posts";

  const showData = async () => {
    const response = await fetch(URL);
    const data = await response.json();
    console.log(data);
    setPosts(data);
  };

  //funcion de busqueda

  const handleClick = () => {
    setSearch(inputRef.current.value);
  };

  // metodo de filtrado (2 formas)

  let filtered_list = [];
  if (!search) {
    filtered_list = posts;
  } else {
    filtered_list = posts.filter((dato) =>
      dato.title.toLowerCase().includes(search.toLocaleLowerCase())
    );
  }

  //   muestra la data
  useEffect(() => {
    showData();
  }, []);

  //renderizamos la vista
  return (
    <div className="container-sm">
      {/* Buscador */}
      <Row>
        <Col>
          <input
            type="text"
            className="form-control"
            placeholder="Buscar"
            ref={inputRef}
            id="filter"
            name="filter"
          />
        </Col>
        <Col className="text-end">
          <Button variant="outline-dark" onClick={handleClick}>
            Buscar
          </Button>
        </Col>
      </Row>
      {/* tabla */}
      <table className="table table-bordered table-hover mt-3 shadow-lg">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {filtered_list.map((post) => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>{post.id}</td>
              <td>
                <Button variant="danger" onClick={handleClick}>
                  Eliminar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Row>
        <Col>
          <input
            type="text"
            className="form-control"
            placeholder="Nombre"
            ref={addName}
            id="name"
            name="name"
          />
        </Col>
        <Col>
          <input
            type="text"
            className="form-control"
            placeholder="Descripción"
            ref={addDescription}
            id="description"
            name="description"
          />
        </Col>
        <Col className="text-end">
          <Button variant="outline-dark" onClick={handleClick}>
            Buscar
          </Button>
        </Col>
      </Row>

    </div>
  );
};

export default SearchComponent;
